//           City: {props.location.city}
//         </Text>
//         <Text color="gray" fontSize="md">
//           id: {props.login.md5}
//         </Text>
//       </VStack>
//       <BasicModal {...props} />
//     </VStack>
//   </AppCard>
// );
const Person = (props) => (
      <ListItem>
        <ListItemAvatar>
        <Avatar alt={props.name.first} src={props.picture.thumbnail} />
        </ListItemAvatar>
        <ListItemText>
        {props.name.title}. {props.name.first} {props.name.last}
        </ListItemText>
        <ListItemText>
        City: {props.location.city}
        </ListItemText>
      </ListItem>
);

function Results() {
  const results = useSelector((state) => state.results.entities.results);
  const status = useSelector((state) => state.results.status);

  console.log(results);
  switch (status) {
    case XHR_STATUS_IDLE:
      return (
        <Alert severity="info">
        <AlertTitle>Info</AlertTitle>
        Please whait.
      </Alert>
      );
    case XHR_STATUS_PENDING:
      return (
        <Alert severity="info">
        <AlertTitle>Info</AlertTitle>
        Data is loading...
      </Alert>
      );
    case XHR_STATUS_REJECTED:
      return (
        <Alert severity="error">
        <AlertTitle>Error</AlertTitle>
        There was an error, oh my :()
      </Alert>
      );
    case XHR_STATUS_FULFILLED:
      return [...results]
        .map((result) => (
          <Box key={result.dob.date}>
            <Person key={result.id.value} {...result} />
          </Box>
        ));
    default:
      return null;
  }
}

export default Results;
