import { createSlice } from "@reduxjs/toolkit";

const slice = createSlice({
  name: "userSettings",
  initialState: {
    sortByAge: "asc",
  },
  reducers: {
    toggleSort(state) {
      state.sortByAge = state.sortByAge === "desc" ? "asc" : "desc";
    },
  },
});
export const { toggleSort } = slice.actions;
export default slice.reducer;
